import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-person',
  templateUrl: './persons.component.html',
  styleUrls: ['./persons.component.css']
})
export class PersonsComponent implements OnInit {
  person: any = {
    name: '',
    personalIdentificationNumber: '',
    address: '',
    nationality: '',
    motherTongue: '',
    mother: null,
    father: null,
    birth: '',
    death: ''
  };
  persons: any[] = [];

  constructor(private httpClient: HttpClient) { }

  ngOnInit(): void {
    // Loads list of persons
    this.loadPersons();
  }

  loadPersons() {
    this.httpClient.get('http://localhost:8080/api/person')
      .subscribe((response: any) => {
        this.persons = response;
      });
  }

  addPerson() {
    const newPerson = { name: this.name };
    this.httpClient.post('http://localhost:8080/api/person', newPerson)
      .subscribe(() => {
        // Updates list of persons
        this.loadPersons();
      });
  }
}
